          'unknown',
          'unknown',
          req,
          { 
            email,
            reason: 'invalid_credentials'
          }
        );

        res.status(401).json({
          error: 'Invalid credentials',
          code: 'INVALID_CREDENTIALS'
        });
      }
    } catch (error) {
      await auditService.logSecurityViolation(
        'authentication_error',
        'unknown',
        'unknown',
        req,
        { error: error instanceof Error ? error.message : 'Unknown error' }
      );

      res.status(500).json({
        error: 'Authentication failed',
        code: 'AUTH_ERROR'
      });
    }
  }
);

/**
 * POST /auth/refresh
 * Refresh JWT access token using refresh token
 */
router.post('/refresh',
  securityMiddleware.validateFields([
    // Validate refresh token format
    body('refreshToken')
      .isUUID()
      .withMessage('Valid refresh token required')
  ]),
  async (req: Request, res: Response): Promise<void> => {
    try {
      const { refreshToken } = req.body;

      if (!refreshToken) {
        res.status(400).json({
          error: 'Refresh token required',
          code: 'REFRESH_TOKEN_MISSING'
        });
        return;
      }

      // Refresh the access token
      const newTokens = jwtService.refreshAccessToken(
        refreshToken,
        req.ip,
        req.get('User-Agent')
      );

      res.json({
        success: true,
        tokens: newTokens
      });
      return;
    } catch (error) {
      await auditService.logAuthenticationEvent(
        'token_refresh',
        'unknown',
        'unknown',
        req,
        { 
          error: error instanceof Error ? error.message : 'Unknown error',
          success: false
        }
      );

      res.status(401).json({
        error: 'Token refresh failed',
        code: 'REFRESH_FAILED',
        message: error instanceof Error ? error.message : 'Unknown error'
      });
      return;
    }
  }
);

/**
 * POST /auth/logout
 * Logout user and revoke tokens
 */
router.post('/logout',
  securityMiddleware.requireAuthentication(),
  async (req: Request, res: Response): Promise<void> => {
    try {
      const user = req.user!;
      const { sessionId } = req.body;

      // Revoke the session
      if (sessionId) {
        jwtService.revokeSession(sessionId);
      } else {
        // Revoke all sessions for user
        jwtService.revokeUserSessions(user.userId);
      }

      // Log logout
      await auditService.logAuthenticationEvent(
        'logout',
        user.userId,
        user.organizationId,
        req
      );

      res.json({
        success: true,
        message: 'Logged out successfully'
      });
    } catch (error) {
      res.status(500).json({
        error: 'Logout failed',
        code: 'LOGOUT_ERROR'
      });
    }
  }
);

/**
 * GET /auth/sessions
 * Get active sessions for user
 */
router.get('/sessions',
  securityMiddleware.requireAuthentication(),
  async (req: Request, res: Response): Promise<void> => {
    try {
      const user = req.user!;
      const sessions = jwtService.getUserSessions(user.userId);

      res.json({
        success: true,
        sessions
      });
    } catch (error) {
      res.status(500).json({
        error: 'Failed to retrieve sessions',
        code: 'SESSIONS_ERROR'
      });
    }
  }
);

