      console.error('Error validating connection:', error);
      res.status(500).json({
        error: 'Failed to validate connection',
        code: 'CONNECTION_VALIDATION_ERROR',
        message: error instanceof Error ? error.message : 'Unknown error'
      });
    }
  }
);

