// Notification Component
const NotificationManager: React.FC = () => {
  const notifications = useNotifications();

  useEffect(() => {
    notifications.forEach(notification => {
      if (notification.type === 'success') {
        toast.success(notification.message, {
          duration: notification.duration || 4000,
        });
      } else if (notification.type === 'error') {
        toast.error(notification.message, {
          duration: notification.duration || 6000,
        });
      } else if (notification.type === 'warning') {
        toast(notification.message, {
          icon: 'âš ï¸',
          duration: notification.duration || 5000,
        });
      } else if (notification.type === 'info') {
        toast(notification.message, {
          icon: 'â„¹ï¸',
          duration: notification.duration || 4000,
        });
      }
    });
  }, [notifications]);

  return null;
};

// Theme Manager
const ThemeManager: React.FC = () => {
  const theme = useTheme();

  useEffect(() => {
    // Apply theme to document
    const root = document.documentElement;

    if (theme.mode === 'dark') {
      root.classList.add('dark');
    } else {
      root.classList.remove('dark');
    }

    // Apply theme colors as CSS custom properties
    root.style.setProperty('--primary', theme.primaryColor);
    root.style.setProperty('--accent', theme.accentColor);
  }, [theme]);

  return null;
};

// Connection Status Manager
const ConnectionManager: React.FC = () => {
  const isAuthenticated = useIsAuthenticated();
  const { setOnlineStatus, setWebsocketStatus } = useUIActions();

  useEffect(() => {
    // Handle online/offline status
    const handleOnline = () => setOnlineStatus(true);
    const handleOffline = () => setOnlineStatus(false);

    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);

    // Set initial online status
    setOnlineStatus(navigator.onLine);

    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, [setOnlineStatus]);

  useEffect(() => {
    if (isAuthenticated) {
      // Connect to WebSocket when authenticated
      websocketService.connect().then(connected => {
        setWebsocketStatus(connected);
      });
    } else {
      // Disconnect WebSocket when not authenticated
      websocketService.disconnect();
      setWebsocketStatus(false);
    }

    return () => {
      if (!isAuthenticated) {
        websocketService.cleanup();
      }
    };
  }, [isAuthenticated, setWebsocketStatus]);

  return null;
};

