const App: React.FC = () => {
  return (
    <ErrorBoundary>
      <HelmetProvider>
        <QueryClientProvider client={queryClient}>
          <Router>
            <Helmet>
              <title>SaaS X-Ray - Automation Discovery Platform</title>
              <meta name="description" content="Discover and monitor automations across your SaaS platforms with enterprise-grade security." />
              <meta name="viewport" content="width=device-width, initial-scale=1" />
            </Helmet>

            {/* Global Managers */}
            <ThemeManager />
            <ConnectionManager />
            <NotificationManager />

            {/* Toast Notifications */}
            <Toaster
              position="top-right"
              toastOptions={{
                duration: 4000,
                style: {
                  background: 'hsl(var(--card))',
                  color: 'hsl(var(--card-foreground))',
                  border: '1px solid hsl(var(--border))',
                },
              }}
            />

            {/* Routes */}
            <Routes>
              {/* Public Routes */}
              <Route path="/login" element={<LoginPage />} />
              <Route path="/oauth/callback" element={<OAuthCallback />} />

              {/* Protected Routes */}
              <Route path="/" element={<Navigate to="/dashboard" replace />} />

              <Route path="/dashboard" element={
                <ProtectedRoute>
                  <DashboardLayout>
                    <DashboardPage />
                  </DashboardLayout>
                </ProtectedRoute>
              } />

              <Route path="/connections" element={
                <ProtectedRoute>
                  <DashboardLayout>
                    <ConnectionsPage />
                  </DashboardLayout>
                </ProtectedRoute>
              } />

              <Route path="/connections/:id" element={
                <ProtectedRoute>
                  <DashboardLayout>
                    <ConnectionsPage />
                  </DashboardLayout>
                </ProtectedRoute>
              } />

              <Route path="/automations" element={
                <ProtectedRoute>
                  <DashboardLayout>
                    <AutomationsPage />
                  </DashboardLayout>
                </ProtectedRoute>
              } />

              <Route path="/security" element={
                <ProtectedRoute>
                  <DashboardLayout>
                    <SecurityPage />
                  </DashboardLayout>
                </ProtectedRoute>
              } />

              <Route path="/analytics" element={
                <ProtectedRoute>
                  <DashboardLayout>
                    <AnalyticsPage />
                  </DashboardLayout>
                </ProtectedRoute>
              } />

              <Route path="/settings" element={
                <ProtectedRoute>
                  <DashboardLayout>
                    <SettingsPage />
                  </DashboardLayout>
                </ProtectedRoute>
              } />

              {/* 404 Route */}
              <Route path="*" element={<NotFoundPage />} />
            </Routes>
          </Router>
        </QueryClientProvider>
      </HelmetProvider>
    </ErrorBoundary>
  );
};

