/**
 * Error severity levels
 */
export enum ErrorSeverity {
  LOW = 'low',
  MEDIUM = 'medium', 
  HIGH = 'high',
  CRITICAL = 'critical',
}

/**
 * Comprehensive error response structure
 */
export interface ErrorResponse {
  /** Whether the request was successful */
  success: false;

  /** Error information */
  error: APIErrorDetails;

  /** Request correlation ID */
  requestId?: string;

  /** Error timestamp */
  timestamp: Date;

  /** API version */
  version: string;
}

/**
 * Detailed error information
 */
export interface APIErrorDetails {
  /** Application-specific error code */
  code: ErrorCode;

  /** Human-readable error message */
  message: string;

  /** HTTP status code */
  statusCode: HTTPStatusCode;

  /** Error severity */
  severity: ErrorSeverity;

  /** Additional error context */
  details?: ErrorDetails;

  /** Field-specific validation errors */
  fieldErrors?: FieldValidationError[];

  /** Suggested actions for resolution */
  suggestions?: string[];

  /** Related documentation links */
  documentationUrls?: string[];

  /** Error trace ID for debugging */
  traceId?: string;

  /** Stack trace (development only) */
  stackTrace?: string;
}

/**
 * Additional error context and metadata
 */
export interface ErrorDetails {
  /** Resource that caused the error */
  resource?: {
    type: string;
    id: string;
    name?: string;
  };

  /** Operation that was attempted */
  operation?: string;

  /** External service error details */
  externalError?: {
    service: string;
    code?: string;
    message?: string;
    statusCode?: number;
  };

  /** Rate limiting information */
  rateLimit?: {
    limit: number;
    remaining: number;
    resetAt: Date;
    retryAfter?: number;
  };

  /** Validation context */
  validation?: {
    schema?: string;
    rule?: string;
    constraint?: string;
  };

  /** Security context */
  security?: {
    ipAddress?: string;
    userAgent?: string;
    attemptCount?: number;
    blockDuration?: number;
  };

  /** Platform-specific error details */
  platform?: {
    name: string;
    error: string;
    code?: string;
    rateLimitHit?: boolean;
    permissionMissing?: string[];
  };
}

/**
 * Field-specific validation error
 */
export interface FieldValidationError {
  /** Field path (dot notation for nested fields) */
  field: string;

  /** Error code specific to this field */
  code: string;

  /** Human-readable error message */
  message: string;

  /** Invalid value that was provided */
  value?: unknown;

  /** Expected format or constraint */
  expected?: string;

  /** Additional validation context */
  context?: Record<string, unknown>;
}

