/**
 * Error factory functions for common error types
 */
export class APIErrorFactory {
  /**
   * Create a validation error
   */
  static validation(
    fieldErrors: FieldValidationError[],
    message = 'Request validation failed'
  ): APIErrorDetails {
    return {
      code: ErrorCode.VALIDATION_ERROR,
      message,
      statusCode: HTTPStatusCode.BAD_REQUEST,
      severity: ErrorSeverity.LOW,
      fieldErrors,
    };
  }

  /**
   * Create an authentication error
   */
  static authentication(
    code: ErrorCode = ErrorCode.INVALID_CREDENTIALS,
    details?: Partial<ErrorDetails>
  ): APIErrorDetails {
    return {
      code,
      message: ERROR_MESSAGES[code],
      statusCode: HTTPStatusCode.UNAUTHORIZED,
      severity: ErrorSeverity.MEDIUM,
      details,
    };
  }

  /**
   * Create an authorization error
   */
  static authorization(
    resource?: string,
    operation?: string
  ): APIErrorDetails {
    return {
      code: ErrorCode.INSUFFICIENT_PERMISSIONS,
      message: ERROR_MESSAGES[ErrorCode.INSUFFICIENT_PERMISSIONS],
      statusCode: HTTPStatusCode.FORBIDDEN,
      severity: ErrorSeverity.MEDIUM,
      details: { resource: { type: resource || 'unknown', id: 'unknown' }, operation },
    };
  }

  /**
   * Create a not found error
   */
  static notFound(
    resourceType: string,
    resourceId: string
  ): APIErrorDetails {
    return {
      code: ErrorCode.USER_NOT_FOUND, // This would be dynamic based on resource type
      message: `${resourceType} not found`,
      statusCode: HTTPStatusCode.NOT_FOUND,
      severity: ErrorSeverity.LOW,
      details: {
        resource: { type: resourceType, id: resourceId },
      },
    };
  }

  /**
   * Create a rate limiting error
   */
  static rateLimited(
    limit: number,
    resetAt: Date,
    retryAfter?: number
  ): APIErrorDetails {
    return {
      code: ErrorCode.RATE_LIMIT_EXCEEDED,
      message: ERROR_MESSAGES[ErrorCode.RATE_LIMIT_EXCEEDED],
      statusCode: HTTPStatusCode.TOO_MANY_REQUESTS,
      severity: ErrorSeverity.MEDIUM,
      details: {
        rateLimit: {
          limit,
          remaining: 0,
          resetAt,
          retryAfter,
        },
      },
    };
  }

  /**
   * Create an internal server error
   */
  static internal(
    message = 'An internal server error occurred',
    traceId?: string
  ): APIErrorDetails {
    return {
      code: ErrorCode.INTERNAL_ERROR,
      message,
      statusCode: HTTPStatusCode.INTERNAL_SERVER_ERROR,
      severity: ErrorSeverity.CRITICAL,
      traceId,
    };
  }
}

