  rateLimit: {
    /** Requests per minute */
    requestsPerMinute: number;

    /** Burst limit */
    burstLimit: number;

    /** Backoff strategy */
    backoffStrategy: 'exponential' | 'linear' | 'fixed';
  };
}

/**
