import { Automation, Platform } from '../models';

/**
 * Slack-specific automation types
 */
export interface SlackAutomation extends Automation {
  platform: 'slack';

  /** Slack-specific metadata */
  slackData: {
    /** Slack team/workspace ID */
    teamId: string;

    /** Slack team name */
    teamName: string;

    /** Slack team domain */
    teamDomain: string;

    /** App or bot information */
    app?: SlackAppInfo;

    /** Workflow information */
    workflow?: SlackWorkflowInfo;

    /** Bot information */
    bot?: SlackBotInfo;

    /** Channel information */
    channels?: SlackChannelInfo[];

    /** User information */
    users?: SlackUserInfo[];
  };
}

/**
 * Slack app information
 */
export interface SlackAppInfo {
  /** App ID */
  id: string;

  /** App name */
  name: string;

  /** App description */
  description?: string;

  /** App developer */
  developer: {
    name: string;
    website?: string;
    supportEmail?: string;
  };

  /** OAuth scopes */
  scopes: string[];

  /** Installation date */
  installedAt: Date;

  /** Installation user */
  installedBy: {
    id: string;
    name: string;
    email?: string;
  };

  /** App distribution */
  distribution: 'workspace' | 'public' | 'private';

  /** App configuration */
  config?: Record<string, unknown>;
}

/**
 * Slack workflow information
 */
export interface SlackWorkflowInfo {
  /** Workflow ID */
  id: string;

  /** Workflow name */
  name: string;

  /** Workflow description */
  description?: string;

  /** Workflow trigger */
  trigger: {
    type: 'webhook' | 'schedule' | 'event' | 'manual';
    config: Record<string, unknown>;
  };

  /** Workflow steps */
  steps: SlackWorkflowStep[];

  /** Created by */
  createdBy: {
    id: string;
    name: string;
  };

  /** Creation date */
  createdAt: Date;

  /** Last run */
  lastRun?: Date;

  /** Run count */
  runCount: number;

  /** Status */
  status: 'active' | 'paused' | 'draft';
}

/**
 * Slack workflow step
 */
export interface SlackWorkflowStep {
  /** Step ID */
  id: string;

  /** Step name */
  name: string;

  /** Step type */
  type: 'message' | 'webhook' | 'app_action' | 'condition' | 'delay';

  /** Step configuration */
  config: Record<string, unknown>;

  /** Connected apps */
  connectedApps?: string[];

  /** External URLs */
  externalUrls?: string[];
}

